<script>
$('.faq-click').click(function(){
if($(this).find('.faq-resposta').css('display') == 'none'){
  $('.faq-less').fadeOut(0);
  $('.faq-plus').fadeIn(0);
  $('.faq-resposta').slideUp();
  $(this).find('.faq-resposta').slideDown();
  $(this).find('.faq-plus').fadeOut(0);
  $(this).find('.faq-less').fadeIn(0);
}else{
  $('.faq-less').fadeOut(0);
  $('.faq-plus').fadeIn(0);
  $('.faq-resposta').slideUp();
}
});
</script>


